import React, { useState } from 'react';
import KeyboardUI from './components/KeyboardUI'; // Import the KeyboardUI component
import { Joystick } from 'react-joystick-component';

function App() {
  return (
    <div style={{ 
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat' }}>
      <GameSelectionScreen activeKey={key}/>
      <MenuSelectionScreen />
      <Joystick size={100} sticky={true} baseColor="red" stickColor="blue" move={console.log('moved')} stop={console.log('stopped')}></Joystick>

    </div>
  );
}

export default App;
