import React from 'react';

const KeyboardUI = () => {
  const handleArrowKeyPress = (keyCode) => {
    const event = new KeyboardEvent('keydown', { keyCode });
    document.dispatchEvent(event);
  };

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <button
        onClick={() => handleArrowKeyPress(38)} // 38 is the keyCode for ArrowUp
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8593;
      </button>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button
          onClick={() => handleArrowKeyPress(37)} // 37 is the keyCode for ArrowLeft
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8592;
        </button>
        <button
          onClick={() => handleArrowKeyPress(39)} // 39 is the keyCode for ArrowRight
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8594;
        </button>
      </div>
      <button
        onClick={() => handleArrowKeyPress(40)} // 40 is the keyCode for ArrowDown
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8595;
      </button>
    </div>
  );
};

export default KeyboardUI;
