import React, { useState, useEffect } from 'react';

const GameSelectionScreen = () => {
  const [cursorPosition, setCursorPosition] = useState(0);
  const icons = [
    { name: 'LinkedIn', link: 'https://www.linkedin.com/' },
    { name: 'GitHub', link: 'https://github.com/' },
    { name: 'Email', link: 'mailto:example@example.com' },
    { name: 'Instagram', link: 'https://www.instagram.com/' },
  ];

  useEffect(() => {
    const handleKeyDown = (event) => {
      if (event.key === 'ArrowLeft') {
        setCursorPosition((prevPosition) => (prevPosition === 0 ? 3 : prevPosition - 1));
      } else if (event.key === 'ArrowRight') {
        setCursorPosition((prevPosition) => (prevPosition === 3 ? 0 : prevPosition + 1));
      }
    };

    document.addEventListener('keydown', handleKeyDown);

    return () => {
      document.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
    <div>
      {icons.map((icon, index) => (
        <a
          key={index}
          href={icon.link}
          className={cursorPosition === index ? 'highlighted' : ''}
        >
          {icon.name}
        </a>
      ))}
    </div>
  );
};

export default GameSelectionScreen;
