import React, { useState, useEffect } from 'react';
import '../assets/pokemon-gameboy-font.css'; 

// Replace this with the path to your menu screen image asset
import selectMenu from '../assets/menu_select.png'

const MenuSelectionScreen = () => {
  const [menuVisible, setMenuVisible] = useState(false);

  // Function to handle the "x" key press
  const handleKeyPress = (event) => {
    if (event.key === 'x') {
      setMenuVisible((prevState) => !prevState);
    }
  };

  // Add an event listener for the "x" key press when the component mounts
  useEffect(() => {
    document.addEventListener('keydown', handleKeyPress);
    // Remove the event listener when the component unmounts
    return () => {
      document.removeEventListener('keydown', handleKeyPress);
    };
  }, []);

  return (
    <div
      style={{
        position: 'absolute',
        top: '20px', // Adjust this value to position the component in the top right
        right: '20px', // Adjust this value to position the component in the top right
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-end',
        zIndex: 9999, // Adjust the z-index value if needed to make sure it overlays other content
      }}
    >
      {/* Hamburger icon to open the menu */}
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          backgroundColor: '#000',
          color: '#fff',
          width: '30px',
          height: '30px',
          borderRadius: '4px',
          cursor: 'pointer',
        }}
        onClick={() => setMenuVisible((prevState) => !prevState)}
      >
        <i className="fas fa-bars"></i>
      </div>

      {/* Menu selection screen */}
      {menuVisible && (
        <div
          style={{
            position: 'relative',
          }}
        >
          <img
            src={selectMenu}
            alt="Menu Selection Screen"
          />
          <div
            className="pokemon-gameboy-font"
            style={{
              position: 'absolute',
              top: '75px', // Adjust this value to position the text vertically
              left: '30px', // Adjust this value to position the text horizontally
              color: 'black', // Text color (white)
              fontSize: '48px', // Font size of the text
              cursor: 'pointer',
            }}
            onClick={() => {
              // Add your logic to navigate to the "Home" page
              console.log('Navigate to Home page');
            }}
          >
            Home
          </div>
          <div
            className="pokemon-gameboy-font"
            style={{
              position: 'absolute',
              top: '140px', // Adjust this value to position the text vertically
              left: '30px', // Adjust this value to position the text horizontally
              color: 'black', // Text color (white)
              fontSize: '48px', // Font size of the text
              cursor: 'pointer',
            }}
            onClick={() => {
              // Add your logic to navigate to the "About" page
              console.log('Navigate to About page');
            }}
          >
            About
          </div>
          <div
            className="pokemon-gameboy-font"
            style={{
              position: 'absolute',
              top: '180px', // Adjust this value to position the text vertically
              left: '30px', // Adjust this value to position the text horizontally
              color: 'black', // Text color (white)
              fontSize: '48px', // Font size of the text
              cursor: 'pointer',
            }}
            onClick={() => {
              // Add your logic to navigate to the "Projects" page
              console.log('Navigate to Projects page');
            }}
          >
            Projects
          </div>
        </div>
      )}

    </div>
  );
};

export default MenuSelectionScreen;
