import React, { useState, useEffect } from 'react';
import { FaLinkedin, FaGithub, FaEnvelope, FaInstagram } from 'react-icons/fa';
import styled from 'styled-components';

const IconContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 50px;
`;

const Icon = styled.div`
  margin: 0 20px;
  cursor: ${props => (props.selected ? 'pointer' : 'default')};
  color: ${props => (props.selected ? 'red' : 'black')};
  animation: ${props => (props.selected ? 'highlight 1s infinite' : 'none')};

  @keyframes highlight {
    0% {
      transform: scale(1);
    }
    50% {
      transform: scale(1.1);
    }
    100% {
      transform: scale(1);
    }
  }
`;

const GameSelectionScreen = () => {
  const [selectedIndex, setSelectedIndex] = useState(0);

  const handleKeyDown = (event) => {
    if (event.key === 'ArrowLeft') {
      setSelectedIndex(prevIndex => (prevIndex === 0 ? 3 : prevIndex - 1));
    } else if (event.key === 'ArrowRight') {
      setSelectedIndex(prevIndex => (prevIndex === 3 ? 0 : prevIndex + 1));
    }
  };

  useEffect(() => {
    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, []);

  return (
      <IconContainer>
        <Icon selected={selectedIndex === 0}>
          <FaLinkedin size={50} />
        </Icon>
        <Icon selected={selectedIndex === 1}>
          <FaGithub size={50} />
        </Icon>
        <Icon selected={selectedIndex === 2}>
          <FaEnvelope size={50} />
        </Icon>
        <Icon selected={selectedIndex === 3}>
          <FaInstagram size={50} />
        </Icon>
      </IconContainer>
  );
};

export default GameSelectionScreen;
