import React from 'react';
import { useSwipeable } from 'react-swipeable';

const KeyboardUI = ({
  onArrowUp,
  onArrowDown,
  onArrowLeft,
  onArrowRight,
}) => {
  const handleArrowKeyPress = (keyCode) => {
    const event = new KeyboardEvent('keydown', { keyCode });
    document.dispatchEvent(event);
  };

  const handleSwipe = (direction) => {
    switch (direction) {
      case 'Up':
        handleArrowKeyPress(38); // 38 is the keyCode for ArrowUp
        break;
      case 'Down':
        handleArrowKeyPress(40); // 40 is the keyCode for ArrowDown
        break;
      case 'Left':
        handleArrowKeyPress(37); // 37 is the keyCode for ArrowLeft
        break;
      case 'Right':
        handleArrowKeyPress(39); // 39 is the keyCode for ArrowRight
        break;
      default:
        break;
    }
  };

  const swipeHandlers = useSwipeable({
    onSwipedUp: () => handleSwipe('Up'),
    onSwipedDown: () => handleSwipe('Down'),
    onSwipedLeft: () => handleSwipe('Left'),
    onSwipedRight: () => handleSwipe('Right'),
  });

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <button
        onClick={onArrowUp}
        {...swipeHandlers}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8593;
      </button>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button
          onClick={onArrowLeft}
          {...swipeHandlers}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8592;
        </button>
        <button
          onClick={onArrowRight}
          {...swipeHandlers}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8594;
        </button>
      </div>
      <button
        onClick={onArrowDown}
        {...swipeHandlers}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8595;
      </button>
    </div>
  );
};

export default KeyboardUI;
