import './css/App.css';
import GameSelectionScreen from './components/GameSelectionScreen';
import background from './assets/sparkles.gif'
import MenuSelectionScreen from './components/MenuSelectionScreen';
import './assets/pokemon-gameboy-font.css';
import React, { useState } from 'react';
import KeyboardUI from './components/KeyboardUI'; // Import the KeyboardUI component
import { Joystick } from 'react-joystick-component';

function App() {
  const [key, setKey] = useState('');

  const handleArrowUp = () => {
    // Handle Arrow Up interaction here
    setKey('up');
  };

  const handleArrowDown = () => {
    // Handle Arrow Down interaction here
    setKey('down');
  };

  const handleArrowLeft = () => {
    // Handle Arrow Left interaction here
    setKey('left');
  };

  const handleArrowRight = () => {
    // Handle Arrow Right interaction here
    setKey('right');
  };

  return (
    <div style={{ 
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat' }}>
      <GameSelectionScreen activeKey={key}/>
      <MenuSelectionScreen />
      <KeyboardUI
        onArrowUp={handleArrowUp}
        onArrowDown={handleArrowDown}
        onArrowLeft={handleArrowLeft}
        onArrowRight={handleArrowRight}
      />
      <Joystick ></Joystick>
    </div>
  );
}

export default App;
