import React, { useEffect, useState } from 'react';

const KeyboardUI = ({ onArrowUp, onArrowDown, onArrowLeft, onArrowRight }) => {
  const [activeKey, setActiveKey] = useState(null);

  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          setActiveKey('up');
          onArrowUp();
          break;
        case 'ArrowDown':
          setActiveKey('down');
          onArrowDown();
          break;
        case 'ArrowLeft':
          setActiveKey('left');
          onArrowLeft();
          break;
        case 'ArrowRight':
          setActiveKey('right');
          onArrowRight();
          break;
        default:
          break;
      }
    };

    const handleKeyUp = () => {
      setActiveKey(null);
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [onArrowUp, onArrowDown, onArrowLeft, onArrowRight]);

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <button
        onMouseDown={onArrowUp}
        onMouseUp={onArrowUp}
        onTouchStart={onArrowUp}
        onTouchEnd={onArrowUp}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
          backgroundColor: activeKey === 'up' ? 'gray' : 'transparent',
        }}
      >
        &#8593;
      </button>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button
          onMouseDown={onArrowLeft}
          onMouseUp={onArrowLeft}
          onTouchStart={onArrowLeft}
          onTouchEnd={onArrowLeft}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
            backgroundColor: activeKey === 'left' ? 'gray' : 'transparent',
          }}
        >
          &#8592;
        </button>
        <button
          onMouseDown={onArrowRight}
          onMouseUp={onArrowRight}
          onTouchStart={onArrowRight}
          onTouchEnd={onArrowRight}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
            backgroundColor: activeKey === 'right' ? 'gray' : 'transparent',
          }}
        >
          &#8594;
        </button>
      </div>
      <button
        onMouseDown={onArrowDown}
        onMouseUp={onArrowDown}
        onTouchStart={onArrowDown}
        onTouchEnd={onArrowDown}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
          backgroundColor: activeKey === 'down' ? 'gray' : 'transparent',
        }}
      >
        &#8595;
      </button>
    </div>
  );
};

export default KeyboardUI;
