import React, { useState, useEffect } from 'react';
import { FaLinkedin, FaGithub, FaEnvelope, FaInstagram } from 'react-icons/fa';
import styled from 'styled-components';
import linkedin from '../assets/linkedin.png'

const IconContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh; /* Set the height of the container to full viewport height */
`;

const Icon = styled.div`
    margin: 0 20px;
    cursor: ${props => (props.selected ? 'pointer' : 'default')};
    color: ${props => (props.selected ? 'red' : 'black')};
    animation: ${props =>
        props.selected ? 'highlight 1s infinite' : props.unselected ? 'fade 0.5s' : 'none'};
    transform: ${props => (props.unselected ? 'scale(0.8)' : 'none')};
    opacity: ${props => (props.unselected ? '0.6' : '1')};

    @keyframes highlight {
        0% {
        transform: scale(1);
        }
        50% {
        transform: scale(1.1);
        }
        100% {
        transform: scale(1);
        }
    }

    @keyframes fade {
        from {
        transform: scale(1);
        opacity: 1;
        }
        to {
        transform: scale(0.8);
        opacity: 0.6;
        }
    }
`;

const GameSelectionScreen = () => {
    const [selectedIndex, setSelectedIndex] = useState(0);

    const handleKeyDown = (event) => {
        if (event.key === 'ArrowLeft') {
        setSelectedIndex(prevIndex => (prevIndex === 0 ? 3 : prevIndex - 1));
        } else if (event.key === 'ArrowRight') {
        setSelectedIndex(prevIndex => (prevIndex === 3 ? 0 : prevIndex + 1));
        }
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
        return () => {
        window.removeEventListener('keydown', handleKeyDown);
        };
    }, []);

    return (
        <IconContainer>
            <Icon selected={selectedIndex === 0} unselected={selectedIndex !== 0}>
                <img src={linkedin} alt="Custom Icon" width={200} height={200}/>
            </Icon>
            <Icon selected={selectedIndex === 1} unselected={selectedIndex !== 1}>
                <FaGithub size={50} />
            </Icon>
            <Icon selected={selectedIndex === 2} unselected={selectedIndex !== 2}>
                <FaEnvelope size={50} />
            </Icon>
            <Icon selected={selectedIndex === 3} unselected={selectedIndex !== 3}>
                <FaInstagram size={50} />
            </Icon>
        </IconContainer>
    );
};

export default GameSelectionScreen;
