import React from 'react';
import { Joystick } from 'react-joystick-component';

const Controls = () => {
  const handleMove = (event) => {
    const directionMap = {
      'UP': 'ArrowUp',
      'DOWN': 'ArrowDown',
      'LEFT': 'ArrowLeft',
      'RIGHT': 'ArrowRight'
    };

    const keyEvent = new KeyboardEvent('keydown', { key: directionMap[event.direction] });
    document.dispatchEvent(keyEvent);
  };

  const handleButtonPress = (key) => {
    const keyEvent = new KeyboardEvent('keydown', { key });
    document.dispatchEvent(keyEvent);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <Joystick 
        size={100} 
        baseColor="red" 
        stickColor="blue" 
        move={handleMove} 
      />
      <div style={{ display: 'flex', justifyContent: 'space-between', width: '200px', marginTop: '20px' }}>
        <button onClick={() => handleButtonPress('z')}>A</button>
        <button onClick={() => handleButtonPress('x')}>B</button>
      </div>
      <button onClick={() => handleButtonPress('c')} style={{ marginTop: '20px' }}>Start</button>
    </div>
  );
};

export default Controls;
