import React, { useEffect } from 'react';

const KeyboardUI = ({
  onArrowUp,
  onArrowDown,
  onArrowLeft,
  onArrowRight,
}) => {
  useEffect(() => {
    const handleKeyDown = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          onArrowUp();
          break;
        case 'ArrowDown':
          onArrowDown();
          break;
        case 'ArrowLeft':
          onArrowLeft();
          break;
        case 'ArrowRight':
          onArrowRight();
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyDown);

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [onArrowUp, onArrowDown, onArrowLeft, onArrowRight]);

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <button
        onClick={onArrowUp}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8593;
      </button>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button
          onClick={onArrowLeft}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8592;
        </button>
        <button
          onClick={onArrowRight}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8594;
        </button>
      </div>
      <button
        onClick={onArrowDown}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8595;
      </button>
    </div>
  );
};

export default KeyboardUI;
