import './css/App.css';
import GameSelectionScreen from './components/GameSelectionScreen';
import background from './assets/sparkles.gif'
import MenuSelectionScreen from './components/MenuSelectionScreen';
import './assets/pokemon-gameboy-font.css';
import React, { useState } from 'react';
import KeyboardUI from './components/KeyboardUI'; // Import the KeyboardUI component
import { Joystick } from 'react-joystick-component';
import DirectionalButtons from './components/DirectionalButtons';

function App() {
  return (
    <div style={{ 
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      height: '100vh', // This makes sure the div takes up the full height of the viewport
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'flex-end',
      alignItems: 'center',
    }}>
      <GameSelectionScreen />
      <MenuSelectionScreen />
      <div style={{marginBottom: '100px'}}> 
        <DirectionalButtons />
      </div>
    </div>

  );
}


export default App;
