import React from 'react';
import { Swipeable } from 'react-swipeable';

const KeyboardUI = ({
  onArrowUp,
  onArrowDown,
  onArrowLeft,
  onArrowRight,
}) => {
  const handleArrowKeyPress = (keyCode) => {
    const event = new KeyboardEvent('keydown', { keyCode });
    document.dispatchEvent(event);
  };

  const handleSwipe = (direction) => {
    switch (direction) {
      case 'Up':
        handleArrowKeyPress(38); // 38 is the keyCode for ArrowUp
        break;
      case 'Down':
        handleArrowKeyPress(40); // 40 is the keyCode for ArrowDown
        break;
      case 'Left':
        handleArrowKeyPress(37); // 37 is the keyCode for ArrowLeft
        break;
      case 'Right':
        handleArrowKeyPress(39); // 39 is the keyCode for ArrowRight
        break;
      default:
        break;
    }
  };

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <Swipeable onSwipedUp={() => handleSwipe('Up')}>
        <button
          onClick={onArrowUp}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8593;
        </button>
      </Swipeable>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <Swipeable onSwipedLeft={() => handleSwipe('Left')}>
          <button
            onClick={onArrowLeft}
            style={{
              width: '40px',
              height: '40px',
              margin: '5px',
            }}
          >
            &#8592;
          </button>
        </Swipeable>
        <Swipeable onSwipedRight={() => handleSwipe('Right')}>
          <button
            onClick={onArrowRight}
            style={{
              width: '40px',
              height: '40px',
              margin: '5px',
            }}
          >
            &#8594;
          </button>
        </Swipeable>
      </div>
      <Swipeable onSwipedDown={() => handleSwipe('Down')}>
        <button
          onClick={onArrowDown}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8595;
        </button>
      </Swipeable>
    </div>
  );
};

export default KeyboardUI;
