import './css/App.css';
import GameSelectionScreen from './components/GameSelectionScreen';
import background from './assets/sparkles.gif'
import MenuSelectionScreen from './components/MenuSelectionScreen';
import './assets/pokemon-gameboy-font.css';
import React, { useState } from 'react';
import Controls from './components/ControlUI';

function App() {
  const [activeKey, setActiveKey] = useState(null);

  const KeyDownContext = React.createContext();

  const handleMove = (event) => {
    setActiveKey(event.direction.toLowerCase());
    handleKeyDown({ key: activeKey });
  };

  const handleButtonPress = (key) => {
    setActiveKey(key);
    handleKeyDown({ key: activeKey });
  };

  const handleKeyDown = event => {
    if (event.key === 'ArrowLeft' || activeKey === 'left') {
      setSelectedIndex(prevIndex => (prevIndex === 0 ? 3 : prevIndex - 1));
      } else if (event.key === 'ArrowRight' || activeKey === 'right') {
      setSelectedIndex(prevIndex => (prevIndex === 3 ? 0 : prevIndex + 1));
      } else if (event.key === 'z' || activeKey === 'z') {
      const selectedIcon = iconRefs.current[selectedIndex];
      if (selectedIcon) {
          selectedIcon.click();
      }
    }
  };

  return (
    <div style={{ 
      backgroundImage: `url(${background})`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      backgroundRepeat: 'no-repeat',
      height: '100vh', // This makes sure the div takes up the full height of the viewport
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'flex-end',
      alignItems: 'center',
    }}>
      <KeyDownContext.Provider value={handleKeyDown}>
        <GameSelectionScreen activeKey={activeKey} />
      </KeyDownContext.Provider>
      <MenuSelectionScreen />
      <div style={{marginBottom: '100px'}}> 
        <Controls onMove={handleMove} onButtonPress={handleButtonPress}/>
      </div>
    </div>

  );
}


export default App;
