import React from 'react';

const KeyboardUI = ({
  onArrowUp,
  onArrowDown,
  onArrowLeft,
  onArrowRight,
}) => {
  const handleArrowKeyPress = (keyCode) => {
    const event = new KeyboardEvent('keydown', { keyCode });
    document.dispatchEvent(event);
  };

  const handleTap = (direction) => {
    switch (direction) {
      case 'Up':
        handleArrowKeyPress(38); // 38 is the keyCode for ArrowUp
        break;
      case 'Down':
        handleArrowKeyPress(40); // 40 is the keyCode for ArrowDown
        break;
      case 'Left':
        handleArrowKeyPress(37); // 37 is the keyCode for ArrowLeft
        break;
      case 'Right':
        handleArrowKeyPress(39); // 39 is the keyCode for ArrowRight
        break;
      default:
        break;
    }
  };

  return (
    <div
      style={{
        position: 'fixed',
        bottom: '20px',
        left: '50%',
        transform: 'translateX(-50%)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        zIndex: 9999,
      }}
    >
      <button
        onClick={() => handleTap('Up')}
        onTouchStart={() => handleTap('Up')}
        onTouchEnd={() => handleTap('Up')}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8593;
      </button>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button
          onClick={() => handleTap('Left')}
          onTouchStart={() => handleTap('Left')}
          onTouchEnd={() => handleTap('Left')}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8592;
        </button>
        <button
          onClick={() => handleTap('Right')}
          onTouchStart={() => handleTap('Right')}
          onTouchEnd={() => handleTap('Right')}
          style={{
            width: '40px',
            height: '40px',
            margin: '5px',
          }}
        >
          &#8594;
        </button>
      </div>
      <button
        onClick={() => handleTap('Down')}
        onTouchStart={() => handleTap('Down')}
        onTouchEnd={() => handleTap('Down')}
        style={{
          width: '40px',
          height: '40px',
          margin: '5px',
        }}
      >
        &#8595;
      </button>
    </div>
  );
};

export default KeyboardUI;
